n","\t#","","\tdeb http://download.proxmox.com/debian/pve $RELEASE pve-no-subscription","\tEOF","","  # create dpkg pre/post install hooks for persistence","  echo \"Creating dpkg hooks in /etc/apt/apt.conf.d ...\"","  cat <<- 'EOF' > \"/etc/apt/apt.conf.d/86pve-nags\"","\tDPkg::Pre-Install-Pkgs {","\t    \"while read -r pkg; do case $pkg in *proxmox-widget-toolkit* | *pve-manager*) touch /tmp/.pve-nag-buster && exit 0; esac done < /dev/stdin\";","\t};","","\tDPkg::Post-Invoke {","\t    \"[ -f /tmp/.pve-nag-buster ] && { /usr/share/pve-nag-buster.sh; rm -f /tmp/.pve-nag-buster; }; exit 0\";","\t};","\tEOF","","  # install the hook script","  temp=''","  if [ \"$1\" = \"--offline\" ]; then","    # packed script requested","    temp=\"$(mktemp)\" && trap \"rm -f $temp\" EXIT","    emit_script > \"$temp\"","  elif [ -f \"pve-nag-buster.sh\" ]; then","    # local copy available","    temp=\"pve-nag-buster.sh\"","  else","    # fetch from github","    echo \"Fetching hook script from GitHub ...\"","    tempd=\"$(mktemp -d)\" &&","      trap \"echo 'Cleaning up temporary files ...'; rm -f $tempd/*; rmdir $tempd\" EXIT","    temp=\"$tempd/pve-nag-buster.sh\"","    wget https://raw.githubusercontent.com/foundObjects/pve-nag-buster/master/pve-nag-buster.sh \\","      -q --show-progress -O \"$temp\"","  fi","  echo \"Installing hook script as /usr/share/pve-nag-buster.sh\"","  install -o root -m 0550 \"$temp\" \"/usr/share/pve-nag-buster.sh\"","","  echo \"Running patch script\"","  /usr/share/pve-nag-buster.sh","","  return 0","}","","# emit a stored copy of pve-nag-buster.sh offline -- this is intended to be used during","# offline provisioning where we don't have access to github or a full cloned copy of the","# project","","# run 'install.sh --emit' to dump stored script to stdout","","# Important: if you're not me you should probably decode this and read it to make sure I'm not doing","#            something malicious like mining dogecoin or stealing your valuable cat pi